import React from 'react';
import '../../Styles/Contact.css';
import { useRef } from 'react';
import emailjs from '@emailjs/browser';


export default function Contact() {
    const formRef=useRef();

    const handlerSubmit= (e)=>{
        e.preventDefault();
        emailjs.sendForm(
            'service_snihe4h',
            'tservice_snihe4h',
            formRef.current,
            'user_uqQfdg8XpW3ZuyDWUlhcj')
        .then((result) => {
          console.log(result.text);
        }, (error) => {
          console.log(error.text);
        });
    };
    
  return (
            <div id="contact"> 
                    <div className="container py-5">
                        <div className="text-center text-white">
                            <h2>Contact Me</h2>
                        </div>
                        <div className="contactParagraph text-center text-white">
                            <p>To contact me and ask for my service, just fill the form and I will contact you as soon as possible.</p>
                        </div>
                        <div className="row">
                            <div className="col-12 col-sm-6 col-lg-6"> 
                                <form ref={formRef}  className="d-flex flex-column justify-content-center">
                                        <input type="text" placeholder="Name" name="user-name" className="form-control bg-transparent "></input>
                                        
                                        <input type="text" placeholder="Subject" name="user-subject" className="form-control bg-transparent"></input>
                                        
                                        <input type="text" placeholder="Email" name="user-email" className="form-control bg-transparent"></input>
                                        
                                </form>
                            </div>
                            <div className="col-12 col-sm-6 col-lg-6">
                                <textarea rows="9" placeholder="Your Message" className="form-control bg-transparent"></textarea>
                            </div>
                            <div className="col-12 d-flex justify-content-center">
                                <button onSubmit={handlerSubmit} className="btn">Send Message</button>
                            </div>
                        </div>
                    </div>
            </div>
    );
}
